import React, { Component } from 'react';
import Web3 from 'web3'

import logo from '../logo.png';
import './App.css';
import HTLC from '../abis/HTLC.json';

class App extends Component {

  async componentWillMount()
  {
    //await this.loadWeb3()
    //await this.loadBlockchainData()
  }

  async loadWeb3()
  {
    if(window.ethereum)
    {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if(window.web3)
    {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else
    {
      window.alert("Web3 not supported in your browser.")
    }
  }

  async loadBlockchainData()
  {
    const web3 = await window.web3

    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })

    console.log(0)
    var netw = ""
    await web3.eth.net.getId().then(net_id => {
      switch (net_id) {
        case 1:
          netw = "Mainnet"
          break
        case 3:
          netw = "Ropsten"
          break
        case 42:
          netw = "Kovan"
          break
        default:
          netw = 'This is an unknown network.'
      }
    })


    console.log(netw)

    this.setState({net: netw})
  }

  async deployHTLC()
  {

    const web3 = await window.web3

    const abi =  HTLC.abi
    const bytecode =  HTLC.bytecode
    //const networkData = HTLC.networks[networkId]

    const deploy_contract = await new web3.eth.Contract(JSON.parse(abi))
    let account = this.state.account; 
    let payload = {
      data: bytecode
     }
    let parameter = {
      from: account,
      gas: web3.utils.toHex(800000),
      gasPrice: web3.utils.toHex(web3.utils.toWei('30', 'gwei')),
      arguments: [this.state.rec_adr, this.state.token_adr, this.state.amount]
    }

    await deploy_contract.deploy(payload).send(parameter, (err, transactionHash) => {
      console.log('Transaction Hash :', transactionHash);
  }).on('confirmation', () => {}).then((newContractInstance) => {
      console.log('Deployed Contract Address : ', newContractInstance.options.address);
  })  


  }

  async startSwap()
  {
    await this.loadWeb3()
    await this.loadBlockchainData()
    await this.deployHTLC()
  }

// seller token: 0x79D93e0b3030a1fd37586aBAD8361DeFc72B35cC
//buyer token: 0x86B88F00a5263a1960b7b5DDBe679234Cd0bd095
  constructor(props)
  {
    super(props)
    this.state = {
      account: "",
      net: "",
      rec_adr: "",
      token_adr: "",
      amount: ""
    }
    this.startSwap = this.startSwap.bind(this)
  }

  render() {
    return (
      <form>
        <h1>Hello</h1>
              <p>Enter the receiver's address:</p>
          <input
          type="text" id="rec_adr"
          />
                    <p>Enter the address of the token to be sent:</p>
          <input
          type="text" id="token_adr"
          />
                    <p>Enter the amount to be sent:</p>
          <input
          type="text" id="amount"
          />

      <div class='form-sub'>
            <button id='subButton' onClick={this.startSwap} type='button'>Start the Swap</button>
        </div>
      </form>
    );

  }
}

export default App;
