import React, { Component } from 'react';
import Web3 from 'web3'
import logo from '../logo.png';
import './App.css';

class App extends Component {

  async componentWillMount()
  {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3()
  {
    if(window.ethereum)
    {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if(window.web3)
    {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else
    {
      window.alert("Web3 not supported in your browser.")
    }
  }

  async loadBlockchainData()
  {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()


    console.log(0)
    var netw
    web3.eth.net.getId().then(net_id => {
      switch (net_id) {
        case 1:
          netw = "Mainnet"
          break
        case 3:
          netw = "Ropsten"
          break
        case 42:
          netw = "Kovan"
          break
        default:
          netw = 'This is an unknown network.'
      }
    })


    console.log(netw)
    this.setState({account: accounts[0]})
    this.setState({net: netw})
  }

  constructor(props)
  {
    super(props)
    this.state = {
      account: "",
      net: ""
    }
  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            Atomic Swap
          </a>
          <ul className= "navbar-nav px-3">
            <li className= "nav-item text-noswap d-none d-sm-none d-sm-block">
              <small className="text-white"> <span id="account"> {this.state.net}</span></small>
            </li>
            </ul>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={logo} className="App-logo" alt="logo" />
                </a>
                <h1>Dapp University Starter Kit</h1>
                <p>
                  Edit <code>src/components/App.js</code> and save to reload.
                </p>
                <a
                  className="App-link"
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  LEARN BLOCKCHAIN <u><b>NOW! </b></u>
                </a>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
