import React, { Component } from 'react';
import Web3 from 'web3'

import logo from '../logo.png';
import './App.css';
import HTLC from '../abis/HTLC.json';

class App extends Component {

  async componentWillMount()
  {
    //await this.loadWeb3()
    //await this.loadBlockchainData()
  }

  async loadWeb3()
  {
    if(window.ethereum)
    {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if(window.web3)
    {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else
    {
      window.alert("Web3 not supported in your browser.")
    }
  }

  async loadBlockchainData()
  {
    const web3 = await window.web3

    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })

    console.log(0)
    var netw = ""
    await web3.eth.net.getId().then(net_id => {
      switch (net_id) {
        case 1:
          netw = "Mainnet"
          break
        case 3:
          netw = "Ropsten"
          break
        case 42:
          netw = "Kovan"
          break
        default:
          netw = 'This is an unknown network.'
      }
    })


    console.log(netw)

    this.setState({net: netw})
  }

  async deployHTLC()
  {

    const web3 = await window.web3

    let abi =  HTLC.abi
    let bytecode =  HTLC.bytecode
    let account = this.state.account; 

  var address = this.state.account

  // Deploy HTLC
  var contract1 = new web3.eth.Contract(abi, address, {gasPrice: '12345678', from: account});
  contract1.deploy({
    data: bytecode,
    arguments: [this.state.rec_adr, this.state.token_adr, this.state.amount]
  })
  .send({
    from: account,
    gas: 1500000,
    gasPrice: '30000000000000'
  }, function(error, transactionHash){  })
  .on('error', function(error){  })
  .on('transactionHash', function(transactionHash){  })
  .on('receipt', function(receipt){
    console.log(receipt.contractAddress) // contains the new contract address
    })
    .on('confirmation', function(confirmationNumber, receipt){  })
    .then(function(newContractInstance){
  console.log(newContractInstance.options.address) // instance with the new contract address
    });

  }


  async approve()
  {
    const web3 = await window.web3
    let contract_address = this.state.token_adr
    let abi = '{ "constant": false, "inputs": [ {"name": "_spender","type": "address" },{"name": "_value","type": "uint256"}],"name": "approve","outputs": [{"name": "","type": "bool" }],"payable": false,"stateMutability": "nonpayable","type": "function"}'
    const token_contract = web3.eth.Contract(abi, contract_address);
  }

  async fund()
  {

  }

  async startSwap()
  {
    await this.loadWeb3()
    await this.loadBlockchainData()
    await this.deployHTLC()
    await this.approve()
    await this.fund()
  }

  constructor(props)
  {
    super(props)
    this.state = {
      account: "",
      net: "",
      rec_adr: "",
      token_adr: "",
      amount: ""
    }
    this.startSwap = this.startSwap.bind(this)
  }

  render() {
    return (
      <form>
        <h1>Hello</h1>
              <p>Enter the receiver's address:</p>
          <input
          type="text" id="rec_adr"
          />
                    <p>Enter the address of the token to be sent:</p>
          <input
          type="text" id="token_adr"
          />
                    <p>Enter the amount to be sent:</p>
          <input
          type="text" id="amount"
          />

      <div class='form-sub'>
            <button id='subButton' onClick={this.startSwap} type='button'>Start the Swap</button>
        </div>
      </form>
    );

  }
}

export default App;
