        <img name="grid5" class="grid-item" src="https://previews.123rf.com/images/alexwhite/alexwhite1211/alexwhite121100559/16225798-buy-now-icon.jpg" onClick={this.interactBox} alt=""></img>
        <img name="grid6" class="grid-item" src="https://previews.123rf.com/images/alexwhite/alexwhite1211/alexwhite121100559/16225798-buy-now-icon.jpg" onClick={this.interactBox} alt=""></img>
        <img name="grid7" class="grid-item" src="https://previews.123rf.com/images/alexwhite/alexwhite1211/alexwhite121100559/16225798-buy-now-icon.jpg" onClick={this.interactBox} alt=""></img>
