import React, { Component } from 'react';
import Web3 from 'web3'
import logo from '../logo.png';
import './App.css';

class App extends Component {

  async componentWillMount()
  {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3()
  {
    if(window.ethereum)
    {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if(window.web3)
    {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else
    {
      window.alert("Web3 not supported in your browser.")
    }
  }

  async loadBlockchainData()
  {
    const web3 = await window.web3
    const accounts = await web3.eth.getAccounts()


    console.log(0)
    var netw = ""
    await web3.eth.net.getId().then(net_id => {
      switch (net_id) {
        case 1:
          netw = "Mainnet"
          break
        case 3:
          netw = "Ropsten"
          break
        case 42:
          netw = "Kovan"
          break
        default:
          netw = 'This is an unknown network.'
      }
    })


    console.log(netw)
    this.setState({account: accounts[0]})
    this.setState({net: netw})
  }
// seller token: 0x79D93e0b3030a1fd37586aBAD8361DeFc72B35cC
//buyer token: 0x86B88F00a5263a1960b7b5DDBe679234Cd0bd095
  constructor(props)
  {
    super(props)
    this.state = {
      account: "",
      net: ""
    }
  }

  render() {
    return (
      <div class="container mt-5">
      <div class="row">
        <div class="col mb-3">
          <h1>React Currency Input Field</h1>
        </div>
      </div>
      <div class="row">
        <div class="col-12">
          <div class="h3">
            Features:
          </div>
          <ul>
            <li>Allows abbreviations eg. 1k = 1,000 2.5m = 2,500,000</li>
            <li>Prefix option eg. £ or $</li>
            <li>Automatically inserts group separator</li>
            <li>Accepts Intl locale config</li>
            <li>Can use arrow down/up to step</li>
            <li>Can allow/disallow decimals</li>
            <li>Written in TypeScript and has type support</li>
            <li>Does not use any third party packages</li>
          </ul>
          <p>
            Please visit the
            <a
              href="https://github.com/cchanxzy/react-currency-input-field/tree/master/src/examples"
              >Github repository</a
            >
            to see the source code from examples.
          </p>
        </div>
      </div>
      <hr />
      <div id="example-1"></div>

      <hr />

      <div id="example-2"></div>

      <hr />

      <div id="example-3"></div>

      <hr />

      <div id="example-4"></div>

      <hr />

      <div id="format-values-example"></div>

      <hr />

      <footer class="mb-5"></footer>
    </div>
    );
  }
}

export default App;
