import React, { Component } from 'react';
import Web3 from 'web3'

import logo from '../logo.png';
import './App.css';
import HTLC from '../abis/HTLC.json';
import ethAds from '../abis/ethereumAds.json';
import ERC20 from '/home/mertmalaz/Desktop/Codes/marketplace/node_modules/@openzeppelin/contracts/build/contracts/ERC20.json';

class App extends Component {

  async componentDidMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
    var c = document.getElementById("grid").children;

    var contract_ethAds = await new web3.eth.Contract(abi, this.state.contract);
    const box_count = await contract_ethAds.methods.getBoxCount().call();
 /*    var i;
    for (i = 0; i < box_count; i++) {
      const box_url = await contract_ethAds.methods.getUrl(i).call();
      c[i].src = box_url;
    } */
  }

  async loadWeb3() {
    await this.setState({ contract: '0x7fbc8d6E241F4962aeE1a7e123708fBbA0608dB6' })
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert("Web3 not supported in your browser.")
    }
  }

  async loadBlockchainData() {
    await this.loadWeb3()
    console.log("Loaded web3")
    const web3 = await window.web3

    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    var netw = ""
    await web3.eth.net.getId().then(net_id => {
      switch (net_id) {
        case 1:
