import React, { Component } from 'react';
import Web3 from 'web3'

import logo from '../logo.png';
import './App.css';
import HTLC from '../abis/HTLC.json';

class App extends Component {

  async loadWeb3()
  {
    if(window.ethereum)
    {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if(window.web3)
    {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else
    {
      window.alert("Web3 not supported in your browser.")
    }
  }

  async loadBlockchainData()
  {
    const web3 = await window.web3

    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })

    console.log(0)
    var netw = ""
    await web3.eth.net.getId().then(net_id => {
      switch (net_id) {
        case 1:
          netw = "Mainnet"
          break
        case 3:
          netw = "Ropsten"
          break
        case 42:
          netw = "Kovan"
          break
        default:
          netw = 'This is an unknown network.'
      }
    })

    this.setState({net: netw})
    console.log(this.state.net)
    console.log(this.state.account)
    console.log(0)
    web3.eth.defaultAccount = this.state.account;
    console.log(0)
  }

  async start_htlc()
  {
    const web3 = await window.web3

    const abi =  HTLC.abi
    const bytecode =  HTLC.bytecode
    const address_htlc = '0x3FA7D29CB0016d4b0e50939E1515B54368B8E173'

    var contract_htlc = new web3.eth.Contract(abi, address_htlc,{gasPrice: '12345678', from: this.state.account})

    contract_htlc.methods.create_htlc([this.state.rec_adr, this.state.token_adr, this.state.amount, this.state.hash])
  }

  async deployHTLC()
  {

    const web3 = await window.web3

    const abi =  HTLC.abi
    const bytecode =  HTLC.bytecode
    let account = this.state.account; 

var address = this.state.account
var contract1 = await new web3.eth.Contract(abi, address, {gasPrice: '12345678', from: account});
 contract1.deploy({
  data: bytecode,
  arguments: ['0xbb17611F0a594f7E37F0B30CCcA3759bC5fDBcD4', '0x752540882984a1422CA0C15F2B34823b29bDF5dd', 100]
})
.send({
  from: account,
  gas: 1500000,
  gasPrice: '30000000000000'
}, function(error, transactionHash){  })
.on('error', function(error){  })
.on('transactionHash', function(transactionHash){  })
.on('receipt', function(receipt){
 console.log(receipt.contractAddress) // contains the new contract address
})
.on('confirmation', function(confirmationNumber, receipt){  })
.then(function(newContractInstance){
  console.log(newContractInstance.options.address) // instance with the new contract address
});
 console.log(contract1.address)
console.log("hey")
  }

  async approve()
  {
    const web3 = await window.web3
    let contract_address = this.state.token_adr
    let abi = '{ "constant": false, "inputs": [ {"name": "_spender","type": "address" },{"name": "_value","type": "uint256"}],"name": "approve","outputs": [{"name": "","type": "bool" }],"payable": false,"stateMutability": "nonpayable","type": "function"}'
    let parsed_abi = JSON.parse(abi)
    const token_contract = web3.eth.Contract(parsed_abi, contract_address);
    token_contract.methods.approve('0xc0b6284f18e1595b93285d396b422e5de227bec96a2dff3d69892efe2031f99c', 100).call();
  }

  async fund()
  {

  }

  async startSwap()
  {
    await this.loadWeb3()
    await this.loadBlockchainData()
    //await this.deployHTLC()
    //await this.approve()
  }

// seller token: 0x79D93e0b3030a1fd37586aBAD8361DeFc72B35cC
//buyer token: 0x86B88F00a5263a1960b7b5DDBe679234Cd0bd095

  constructor(props)
  {
    super(props)
    this.state = {
      account: "",
      net: "",
      rec_adr: "",
      token_adr: "",
      amount: "",
      hash: ""
    }
    this.startSwap = this.startSwap.bind(this)
  }



  render() {
    return (
      <form>
        <h1>Hello</h1>
              <p>Enter the receiver's address:</p>
          <input
          type="text" id="rec_adr"
          />
                    <p>Enter the address of the token to be sent:</p>
          <input
          type="text" id="token_adr"
          />
                    <p>Enter the amount to be sent:</p>
          <input
          type="text" id="amount"
          />

      <div class='form-sub'>
            <button id='subButton' onClick={this.startSwap} type='button'>Start the Swap</button>
        </div>
      </form>
    );

  }
}

export default App;
